    return <div className="Note" style={backgroundColor = bgColor() }    > 
        <span> {id}</span>
        <span> {text} </span>
        <div className="note-footer">
            <small>{date}</small>
            <MdDelete className='delete-icon' size = '16'/>
        </div>
    </div>

}



