import { MdDelete } from "react-icons/md";
import './../index.css'
const add = ({id , text, date}) => {
    return <div className="Note" style={{backgroundColor: ramdomColor }}> 
        <span> {text} </span>
        <div className="note-footer">
            <small>{date}</small>
            <MdDelete className='delete-icon' size = '16'/>
        </div>
    </div>
}


const colors_block = [
    '#93B5C6',
    '#C9CCD5',
    '#E4D8DC',
    '#FFE3E3'
  ];
  const ramdomColor = colors_block[Math.floor(Math.random() * colors_block.length)]


export default add;
