import { MdDelete } from "react-icons/md";
import './../index.css'

const Note = ({id , text, date , handleDeleteNote}) => {
   
    return <div className="note"    > 
        <span> {text} </span>
        <div className="note-footer">
            <small>{date}</small>
            <MdDelete className='delete-icon' size = '16' onClick={ () => handleDeleteNote(id)}/>
        </div>
    </div>

}



